// Get gate state from data
let state = 0;
if (data.series && data.series.length > 0) {
  const series = data.series[0];
  if (series.fields && series.fields.length > 1) {
    const values = series.fields[1].values;
    if (values && values.length > 0) {
      state = values.buffer ? values.buffer[values.length - 1] : values[values.length - 1];
    }
  }
}

const left = svgnode.findOne('#gate-left');
const right = svgnode.findOne('#gate-right');
const text = svgnode.findOne('#gate-status');

if (left && right && text) {
  if (state === 2) {
    // Open - green
    left.animate(500).transform({translateX: -100});
    right.animate(500).transform({translateX: 100});
    left.findOne('rect').fill('rgba(34, 197, 94, 0.3)').stroke('#22c55e');
    right.findOne('rect').fill('rgba(34, 197, 94, 0.3)').stroke('#22c55e');
    text.text('OPEN').fill('#22c55e');
  } else if (state === 1) {
    // Moving - yellow
    left.animate(500).transform({translateX: -50});
    right.animate(500).transform({translateX: 50});
    left.findOne('rect').fill('rgba(234, 179, 8, 0.3)').stroke('#eab308');
    right.findOne('rect').fill('rgba(234, 179, 8, 0.3)').stroke('#eab308');
    text.text('MOVING').fill('#eab308');
  } else {
    // Closed - purple/blue
    left.animate(500).transform({translateX: 0});
    right.animate(500).transform({translateX: 0});
    left.findOne('rect').fill('rgba(99, 102, 241, 0.3)').stroke('#6366f1');
    right.findOne('rect').fill('rgba(99, 102, 241, 0.3)').stroke('#6366f1');
    text.text('CLOSED').fill('#94a3b8');
  }
}

options.getOption:
// Get the match rate
let rate = 0;
if (data.series && data.series.length > 0) {
  const series = data.series[0];
  if (series.fields && series.fields.length > 1) {
    const values = series.fields[1].values;
    if (values && values.length > 0) {
      const val = values.buffer ? values.buffer[values.length - 1] : values[values.length - 1];
      rate = Math.min(100, Math.max(0, val || 0));
    }
  }
}

return {
  series: [{
    type: 'gauge',
    startAngle: 200,
    endAngle: -20,
    min: 0,
    max: 100,
    radius: '90%',
    progress: {
      show: true,
      width: 12,
      itemStyle: {
        color: rate > 90 ? '#22c55e' : rate > 70 ? '#eab308' : '#ef4444'
      }
    },
    axisLine: {
      lineStyle: {
        width: 12,
        color: [[1, 'rgba(100,100,100,0.3)']]
      }
    },
    axisTick: { show: false },
    splitLine: { show: false },
    axisLabel: { show: false },
    pointer: { show: false },
    anchor: { show: false },
    title: {
      show: true,
      offsetCenter: [0, '70%'],
      fontSize: 12,
      color: '#94a3b8'
    },
    detail: {
      valueAnimation: true,
      fontSize: 24,
      fontWeight: 'bold',
      offsetCenter: [0, '0%'],
      formatter: '{value}%',
      color: rate > 90 ? '#22c55e' : rate > 70 ? '#eab308' : '#ef4444'
    },
    data: [{ value: rate.toFixed(0), name: 'ACC Match' }]
  }]
};

