SELECT ROUND(
  COUNT(*) FILTER (WHERE outcome = 'paid_exit')::numeric /
  NULLIF(COUNT(*) FILTER (WHERE outcome IS NOT NULL), 0) * 100, 1
) as paid_rate
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours';

SELECT ROUND(
  COUNT(*) FILTER (WHERE tailgated = true)::numeric /
  NULLIF(COUNT(*), 0) * 100, 1
) as tailgate_rate
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours' AND outcome IS NOT NULL;

SELECT ROUND(
  COUNT(*) FILTER (WHERE outcome = 'lost_unauthorized')::numeric /
  NULLIF(COUNT(*) FILTER (WHERE outcome IS NOT NULL), 0) * 100, 1
) as lost_rate
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours';

SELECT ROUND(
  COUNT(*) FILTER (WHERE outcome = 'unpaid_exit')::numeric /
  NULLIF(COUNT(*) FILTER (WHERE outcome IS NOT NULL), 0) * 100, 1
) as unpaid_rate
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours';

SELECT ROUND(AVG(duration_ms) / 1000.0, 1) as avg_duration
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours' AND duration_ms IS NOT NULL;

SELECT ROUND(AVG(total_pos_dwell_ms) / 1000.0, 1) as avg_pos_dwell
FROM person_journeys
WHERE site = 'netto' AND time > NOW() - INTERVAL '24 hours' AND total_pos_dwell_ms IS NOT NULL;
